import React, { useState } from 'react';
import CalcDisplay from './CalcDisplay';
import CalcButtonLine from './CalcButtonLine';
import CalcButton from './CalcButton';
import pipe from 'ramda/src/pipe';

const Calculator = ({ expression, onClickCalcButton }) => (
  <div className="calc">
    <CalcDisplay text={expression} />
    <div className="calc__buttons">
      <CalcButtonLine>
        <CalcButton text="C" onClick={onClickCalcButton} />
        <CalcButton text="AC" onClick={onClickCalcButton} />
        <CalcButton text="%"  onClick={onClickCalcButton} />
        <CalcButton text="รท"  onClick={onClickCalcButton} />
      </CalcButtonLine>
      <CalcButtonLine>
        <CalcButton text="7" onClick={onClickCalcButton} />
        <CalcButton text="8" onClick={onClickCalcButton} />
        <CalcButton text="9" onClick={onClickCalcButton} />
        <CalcButton text="X" onClick={onClickCalcButton} />
      </CalcButtonLine>
      <CalcButtonLine>
        <CalcButton text="4" onClick={onClickCalcButton} />
        <CalcButton text="5" onClick={onClickCalcButton} />
        <CalcButton text="6" onClick={onClickCalcButton} />
        <CalcButton text="-" onClick={onClickCalcButton} />
      </CalcButtonLine>
      <CalcButtonLine>
        <CalcButton text="1" onClick={onClickCalcButton} />
        <CalcButton text="2" onClick={onClickCalcButton} />
        <CalcButton text="3" onClick={onClickCalcButton} />
        <CalcButton text="+" onClick={onClickCalcButton} />
      </CalcButtonLine>
      <CalcButtonLine>
        <CalcButton text="0" onClick={onClickCalcButton} />
        <CalcButton text="," onClick={onClickCalcButton} />
        <CalcButton text="=" onClick={onClickCalcButton} />
      </CalcButtonLine>
    </div>
  </div>
);

// constants
const OPERATIONS = {
  '+': () => null,
  'X': () => null,
  '-': () => null,
  '%': () => null,
  'รท': () => null
};

const ACTIONS = {
  '=':  () => console.error('the button "=" is not yet implemented'),
  'C':  () => console.error('the button "C" is not yet implemented'),
  'AC': () => console.error('the button "AC" is not yet implemented')
};

// helpers
const isNumber = number => !isNaN(number);

const isOperation = button => !!OPERATIONS[button];

const sanitizeExpression = expression => button => { 
  console.log(button)
  return expression === '0' ? button : expression + button;
}

// const parseExpression = expression => expression.split('').map(
//   item => isNumber(item) ? item : isOperation(item) ? OPERATIONS[item] : ''
// );

const parseExpression = expression => {
  const expInArray = expression.split('').map(
    item => isNumber(item) ? item : isOperation(item) ? OPERATIONS[item] : ''
  );

  let result = [];
  let numbers = [];
  let counter = 0;
  while (counter < expInArray.length) {
    const item = expInArray[counter];

    if(isNumber(item)) {
      numbers.push(item)
    } else {
      result.push(numbers);
      result.push(item)
      numbers = [];
    }
    counter++;
  }

  if (numbers.length) {
    result.push(numbers);
  }

  return result;
};

const CalculatorContainer = () => {

  const [expression, setExpression] = useState('0');
  const [parsedExpression, setParsed] = useState([]);

  console.log(parsedExpression);

  const onClickCalcButton = button => {
    const action = ACTIONS[button];

    if (action) {
      action();
      return
    }

    const sanitizedExpression = pipe(
      sanitizeExpression(button),
      sanitized => {
        setExpression(sanitized);
        return sanitized;
      },
    )(expression)

    pipe(
      parseExpression,
      setParsed
    )(sanitizedExpression);
  };

  return <Calculator 
    expression={expression}
    onClickCalcButton={onClickCalcButton}
  />
}

export default CalculatorContainer;
